(()=>{document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".filter-btn"),c=document.querySelectorAll(".project-card");e.forEach(i=>{i.addEventListener("click",function(){const s=this.getAttribute("data-filter");e.forEach(t=>t.classList.remove("active")),this.classList.add("active"),c.forEach(t=>{const n=t.getAttribute("data-tags");s==="all"||n.includes(s)?t.classList.remove("hidden"):t.classList.add("hidden")})})})});})();
